
public class Week9JavaPt53 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int a=1; a<=6; a++)
		{
		for (int b=6; b>a; b--)
		{
		System.out.print(" ");
		}
		for (int c=1; c<=a-1; c++)
		{
		System.out.print("*");
		}
		System.out.println();
		}
	}
}

	














	


