
public class Week9Pt55Alternative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//making space
		
				String Star  = "     ";
				String Star1 = "    ";
				String Star2 = "   ";
				String Star3 = "  ";
				String Star4 = " ";
				String Star5 = "";
				
				//making star symbols 
				
				String starLight;	
				String Starry  = "1";
				String Starry1 = "222";
				String Starry2 = "33333";
				String Starry3 = "4444444";
				String Starry4 = "555555555";
				String Starry5 = "66666666666";
				
				
				// loops to add each new line with it's corresponding space
				
				for (int i=0; i<1; i++) {
					starLight = Star += Starry;

					System.out.println(starLight);
					
					for (int j=0; j<1; j++) {
						starLight = Star1 += Starry1;

						System.out.println(starLight);
						
						for (int k=0; k<1; k++) {
							starLight = Star2 += Starry2;

							System.out.println(starLight);
							
							for (int l=0; l<1; l++) {
								starLight = Star3 += Starry3;

								System.out.println(starLight);
								
								for (int n=0; n<1; n++) {
									starLight = Star4 += Starry4;

									System.out.println(starLight);
									
									for (int o=0; o<1; o++) {
										starLight = Star5 += Starry5;

										System.out.println(starLight);
								
									}
								}
							}
						}
					}
				}
			}

	}

